<template>  <div class="app-head">    <div class="head-tool">      <div class="side-switch" @click="toggleSide">        <i class="iconfont icon-bars"></i>      </div>      <div class="app-name">{{appName}}</div>    </div>  </div></template><script>	export default {		name: "index",    computed: {			appName: function () {        return this.$store.state.app.appName;			}    },    methods: {	    toggleSide: function () {        this.$store.dispatch("toggleSideBar");	    }    }	}</script><style scoped>  .head-tool{    padding: 5px 0;    border-bottom: 1px solid #eee;  }  .app-name{    font-size: 20px;    font-weight: 600;    line-height: 40px;  }  .side-switch{    float: left;    padding: 5px;    height: 30px;    line-height: 30px;    margin-right: 20px;    cursor: pointer;  }  .side-switch:hover{    color: green;  }</style>