<template>  <el-container style="height: 100%" :style="{color: color}">    <el-aside :width="sidebar.opened ? sideWidth : sideCloseWidth" :style="{backgroundColor: sideBack}" class="aside-animate">      <sidebar/>    </el-aside>    <el-container>      <el-header :style="{backgroundColor: headerBack}">        <head-bar/>      </el-header>      <el-main :style="{backgroundColor: mainBack}">        main      </el-main>    </el-container>  </el-container></template><script>  import Sidebar from "./Sidebar"  import HeadBar from "./Header"	export default {		name: "Layout",    components: {			Sidebar, HeadBar    },    computed: {			sideWidth: function () {        return this.$store.state.app.sideWidth;			},	    sideCloseWidth: function () {		    return this.$store.state.app.sideCloseWidth;	    },	    sidebar: function () {		    return this.$store.state.app.sidebar;	    },      sideBack: function () {        return this.$store.state.app.theme.sideBack      },      headerBack: function () {        return this.$store.state.app.theme.headerBack      },	    mainBack: function () {		    return this.$store.state.app.theme.mainBack	    },	    color: function () {		    return this.$store.state.app.theme.color	    },    }	}</script><style scoped>  .aside-animate{    transition: width .5s;  }</style>